import React, { Component, useState } from "react";
import {Redirect} from "react-router-dom";

function Login ( ) {
    // constructor(props) {
    //     super(props);
    //     this.state = {
    //       redirect:false    
    //     };
    //   }
     const state = {
        credentials: {username: '', password: ''}
    }
    login = event => {
        console.log('submitted!');
    }

    inputChanged = event => {
        const cred = state.credentials;
        cred[event.target.name] = event.target.value;
        setState({credentials: cred});
      }
      onSubmit = async event => {
        event.preventDefault();
        console.log(state.credentials.username , state.credentials.password );
        const u  = state.credentials.username;
        const p  =  state.credentials.password
        const response = await fetch('asap.carriot.ir/login' ,{
            method: 'POST',
            Headers :{
                'Content-Type': 'application/json'
            } ,
            credentials : 'include',
            body: JSON.stringify(
                { u , p}
            )
        });
        const content  = await response.json();
        console.log(content);  //  to check if we get esponse from server or not
        // if(content.status === 'success'){
        //     this.setState({redirect:true});

        // }

    }
    // if (redirect) {
    //     return <Redirect to="/"/>;
    // }



        return (
            <form  onSubmit={this.onSubmit}>
                <h3>Log in</h3>
                <div className="form-group" >
                    <label>Username</label>
                    <input type="username" className="form-control" placeholder="Enter username"  name="username" value={this.state.credentials.username}  onChange={this.inputChanged} />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" placeholder="Enter password" id = "password" name="password" value={this.state.credentials.password} onChange={this.inputChanged}   />
                </div>

                <div className="form-group">
                    <div className="custom-control custom-checkbox">
                        <input type="checkbox" className="custom-control-input" id="customCheck1" />
                        <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                    </div>
                </div>
                <button type="submit" onClick={this.login} className="btn btn-dark btn-lg btn-block"  >Sign in</button>
            </form>
        );
    }
    export default Login;
